function execGit(cmd, cb){
	console.log(cmd.green.bold.inverse)
	function puts(error, stdout, stderr) {
		if(error) cb(error, null)
		sys.puts(stdout) 
		cb(null, stdout);
	}
	exec(cmd, puts)	

}

